package me.simon76800.library.mob.player.skin;

import java.awt.Color;
import java.util.ArrayList;
import java.util.Arrays;
import java.util.HashMap;
import me.simon76800.library.mob.player.Races;

public final class SkinCustomization {
	public static final HashMap<Races, ArrayList<Color>> SKIN_COLOURS = new HashMap<Races, ArrayList<Color>>();
	public static final HashMap<Races, ArrayList<Color>> EYE_COLOURS = new HashMap<Races, ArrayList<Color>>();
	private static final HashMap<Integer, String> HUMAN_SKIN_VALUES = new HashMap<Integer, String>();
	private static final HashMap<Integer, String> HUMAN_SKIN_SIGNATURES = new HashMap<Integer, String>();

	/**
	 * Initializes all skins
	 * 
	 * Use https://mineskin.org/ to retrieve texture values and signatures from uploaded skins
	 */
	public static void init() {
		SKIN_COLOURS.put(Races.HUMAN, new ArrayList<Color>(Arrays.asList(new Color(250, 220, 196),
				new Color(245, 187, 149), new Color(203, 137, 103), new Color(127, 67, 41))));

		EYE_COLOURS.put(Races.HUMAN, new ArrayList<Color>(
				Arrays.asList(new Color(79, 57, 26), new Color(64, 128, 206), new Color(99, 173, 86))));

		HUMAN_SKIN_VALUES.put(Integer.valueOf(0),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(0),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(1),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(1),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(2),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(2),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(10),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(10),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(11),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(11),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(12),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(12),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(20),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(20),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(21),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(21),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(22),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(22),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(30),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(30),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(31),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(31),
				"");
		HUMAN_SKIN_VALUES.put(Integer.valueOf(32),
				"");
		HUMAN_SKIN_SIGNATURES.put(Integer.valueOf(32),
				"");
	}

	/**
	 * 
	 * @return corresponding skin value from given characteristics
	 */
	public static String getSkinValue(Races race, int skinColour, int eyeColour) {
		return (String) HUMAN_SKIN_VALUES.get(Integer.valueOf(skinColour * 10 + eyeColour));
	}

	/**
	 * 
	 * @return corresponding skin signature from given characteristics
	 */
	public static String getSkinSignature(Races race, int skinColour, int eyeColour) {
		return (String) HUMAN_SKIN_SIGNATURES.get(Integer.valueOf(skinColour * 10 + eyeColour));
	}
}
